mapper.select:
SELECT COUNT(*)
FROM MEETING_JOIN
WHERE MEETING_NO = #{mno}

mapper.delete:
DELETE MEETING_JOIN
WHERE MEETING_NO = #{meetingNo}
    AND USER_NO = #{userNo}

mapper.select:
SELECT CASE WHEN COUNT(*) = 1 THEN 1 ELSE 0 END AS JOINCOUNT
FROM MEETING_JOIN 
WHERE MEETING_NO = #{meetingNo}
    AND USER_NO = #{userNo}
