mapper.update:
UPDATE MEMBER
SET ADDRESS = #{address}
WHERE USER_ID = #{userId}

UPDATE MEMBER
SET USER_PWD = #{userPwd}
WHERE USER_ID = #{userId}

UPDATE MEMBER
SET USER_PWD = #{encPwd}
WHERE USER_ID = #{userId}

UPDATE MEMBER
SET STATUS = 'N'
WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_ID
FROM MEMBER
WHERE USER_NAME = #{userName}
AND EMAIL = #{email}

SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID = #{id}
AND USER_NAME = #{name}
AND STATUS = 'Y'

mapper.update:
UPDATE MEMBER
SET SUBSCRIBE = 'Y'
WHERE USER_ID = #{userId}

UPDATE RESERVATION
SET STATUS = 'N'
WHERE RESERVATION_ID = #{reservationId}

UPDATE TICKET
SET STATUS = 'N'
WHERE RESERVATION_ID = #{reservationId}

UPDATE PAYMENTS
SET STATUS = 'N'
WHERE RESERVATION_ID = #{reservationId}

UPDATE SEATS
SET STATUS = 'Y'
WHERE SEAT_ID = #{seatId}

mapper.select:
SELECT COUNT(*)
FROM CHAT_JOIN
WHERE USER_NO = #{userNo}

SELECT COUNT(*)
FROM CHAT
WHERE USER_NO = #{userNo}

mapper.update:
UPDATE COUPON
SET STATUS = 'N'
WHERE COUPON_NO = #{couponNo}
